if(msg.statusCode!==200) {
    global.set('priceSet',0);
    node.warn('Får ej kontakt med Tibber servern. Ställer in reglering till 0.')
    return;
}
var today = msg.payload.data.viewer.homes[0].currentSubscription.priceInfo.today;
var tomorrow;
var current = msg.payload.data.viewer.homes[0].currentSubscription.priceInfo.current.energy*100;
var consumption = msg.payload.data.viewer.homes[0].consumption.nodes;

node.send({payload:[]});
if(consumption!==undefined) {
for( var i = 0; i < consumption.length; i++){
    var timestamp = toTimestamp(consumption[i].from)
    var topic = "Förbrukning"
    var value = consumption[i].consumption;
    node.send({topic:topic,timestamp:timestamp,payload:value})
}
}

if(msg.payload.data.viewer.homes[0].currentSubscription.priceInfo.tomorrow!==undefined) {
    tomorrow = msg.payload.data.viewer.homes[0].currentSubscription.priceInfo.tomorrow;
}
var priceArray = today.concat(tomorrow);
priceArray.sort(function(a,b){return a.energy - b.energy});
var time = toTimestamp(new Date());
var lowestPrice = toTimestamp(priceArray[0].startsAt);
var Min = priceArray[config.plugins.tibber.price_min].energy*100;
var Max = priceArray[(priceArray.length-1)-config.plugins.tibber.price_max].energy*100;
for( var i = 0; i < priceArray.length; i++){
    var timestamp = toTimestamp(priceArray[i].startsAt)
    var topic = "Pris"
    var adjust = 0;
    var hotwater_adjust = Number(config.plugins.tibber.hotwater_price_normal);
    var value = Number(priceArray[i].energy*100).toFixed(2);
    if(value>Max) {
        hotwater_adjust = Number(config.plugins.tibber.hotwater_price_high);
        adjust = config.plugins.tibber.adjust_high;
    }
    if(value<=(config.plugins.tibber.price_low)) {
        hotwater_adjust = Number(config.plugins.tibber.hotwater_price_superlow);
        adjust = config.plugins.tibber.adjust_superlow;
    } else if(timestamp>=lowestPrice) {
        if(value<Min) {
            hotwater_adjust = Number(config.plugins.tibber.hotwater_price_low);
            adjust = config.plugins.tibber.adjust_low;
        }
    }
    if((Max-Min)<config.plugins.tibber.adjust_active) {
        hotwater_adjust = Number(config.plugins.tibber.hotwater_price_normal);
        adjust = 0;
    }
    if(config.plugins.tibber.hotwater_active===true) {
        node.send({topic:"Varmvattenläge",timestamp:timestamp,payload:hotwater_adjust})
    }
    node.send({topic:"Nedre Brytpunkt",timestamp:timestamp,payload:Min})
    node.send({topic:"Övre Brytpunkt",timestamp:timestamp,payload:Max})
    node.send({topic:topic,timestamp:timestamp,payload:value})
    node.send({topic:"Kurvjustering",timestamp:timestamp,payload:adjust})

}
node.send({topic:"Nuvarande Pris",payload:current})
if(current<=(config.plugins.tibber.price_low)) {
    node.send([null,null,{payload:"super_low"}])
    node.send([null,{payload:`Super lågt elpris: ${(current).toFixed(2)} öre`}])
} else if(current<Min) {
    if(time>lowestPrice) {
        node.send([null,null,{payload:"low"}])
    }
    node.send([null,{payload:`Lågt elpris: ${(current).toFixed(2)} öre`}])
} else if(current>Max) {
    node.send([null,null,{payload:"high"}])
    node.send([null,{payload:`Högt elpris: ${(current).toFixed(2)} öre`}])
} else {
    node.send([null,null,{payload:"normal"}])
    node.send([null,{payload:`Normalt elpris: ${(current).toFixed(2)} öre`}])
}
function toTimestamp(strDate){
 var datum = Date.parse(strDate);
 return Number(datum.toFixed());
}

